package org.r4fter.licensing.validation;

import static org.hamcrest.MatcherAssert.assertThat;
import static org.hamcrest.Matchers.is;
import static org.junit.jupiter.api.Assertions.assertThrows;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

class LicenseValidatorTest {

    private static final String USER_NAME = "user@email.com";

    private LicenseValidator licenseValidator;

    @BeforeEach
    void setUp() throws Exception {
        licenseValidator = new LicenseValidator();
    }

    @Test
    void validator_should_verify_a_correct_license() throws Exception {
        final boolean valid = licenseValidator.validate(USER_NAME, getCorrectLicenseSignature());

        assertThat(valid, is(true));
    }

    @Test
    void validator_should_deny_an_incorrect_license() throws Exception {
        final boolean valid = licenseValidator.validate(USER_NAME, getIncorrectLicenseSignature());

        assertThat(valid, is(false));
    }

    private String getCorrectLicenseSignature() {
        return "";
    }

    private String getIncorrectLicenseSignature() {
        return "";
    }
}